import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Inicio() {
  const [stats, setStats] = useState({
    usuarios: 0,
    tecnicos: 0,
    enProceso: 0,
    cerrados: 0,
    cancelados: 0,
    promedioSatisfaccion: 0,
  });

  const [statsTecnicos, setStatsTecnicos] = useState([]);
  const [statsUsuarios, setStatsUsuarios] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError("");
      try {
        const [
          usuariosRes,
          tecnicosRes,
          enProcesoRes,
          cerradosRes,
          canceladosRes,
          evaluacionesRes,
          tecnicosStatsRes,
          usuariosStatsRes,
        ] = await Promise.all([
          axios.get("http://localhost:3001/web/usuarios"),
          axios.get("http://localhost:3001/web/tecnicos"),
          axios.get("http://localhost:3001/web/ticketsEnProceso"),
          axios.get("http://localhost:3001/web/ticketsCerrado"),
          axios.get("http://localhost:3001/web/ticketsCancelado"),
          axios.get("http://localhost:3001/web/ticketsConEvaluacion"),
          axios.get("http://localhost:3001/web/statsTecnicos"),
          axios.get("http://localhost:3001/web/statsUsuarios"),
        ]);

        // calcular promedio con protección
        const promedio = calcularPromedio(evaluacionesRes?.data || []);

        // usuarios y tecnicos filtrando por id_rol (asegúrate que backend devuelve id_rol)
        const usuariosList = Array.isArray(usuariosRes?.data) ? usuariosRes.data : [];
        const usuariosCount = usuariosList.filter((u) => Number(u.id_rol) === 3).length;
        const tecnicosCount = usuariosList.filter((u) => Number(u.id_rol) === 2).length;

        setStats({
          usuarios: usuariosCount,
          tecnicos: tecnicosCount,
          enProceso: Array.isArray(enProcesoRes?.data) ? enProcesoRes.data.length : 0,
          cerrados: Array.isArray(cerradosRes?.data) ? cerradosRes.data.length : 0,
          cancelados: Array.isArray(canceladosRes?.data) ? canceladosRes.data.length : 0,
          promedioSatisfaccion: promedio,
        });

        // Normalizar los stats de técnicos y usuarios por si los nombres cambian
        setStatsTecnicos(normalizeTecnicosStats(tecnicosStatsRes?.data || []));
        setStatsUsuarios(normalizeUsuariosStats(usuariosStatsRes?.data || []));
      } catch (err) {
        console.error("Error cargando dashboard:", err);
        setError("Ocurrió un error al cargar los datos. Revisa la consola (Network/Console).");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const calcularPromedio = (lista) => {
    if (!Array.isArray(lista) || lista.length === 0) return 0;
    // algunos endpoints devuelven objetos con distintos nombres
    const evaluados = lista.filter((t) => {
      const v = t?.calificacion_promedio ?? t?.calificacion ?? t?.avg_calificacion ?? null;
      return v !== null && v !== undefined;
    });
    if (evaluados.length === 0) return 0;

    const total = evaluados.reduce((sum, t) => {
      const val = Number(t?.calificacion_promedio ?? t?.calificacion ?? t?.avg_calificacion ?? 0);
      return sum + (isNaN(val) ? 0 : val);
    }, 0);

    const avg = total / evaluados.length;
    return Number.isFinite(avg) ? avg.toFixed(2) : 0;
  };

  // Normalizadores defensivos para los endpoints personalizados
  const normalizeTecnicosStats = (arr) => {
    // Esperado: [{ id_usuario, nombre, tickets_resueltos, calificacion_promedio }, ...]
    return (arr || []).map((t) => ({
      id_usuario: t.id_usuario ?? t.id ?? t.id_tecnico ?? null,
      nombre: t.nombre ?? t.nombre_usuario ?? t.nombre_tecnico ?? "Sin nombre",
      tickets_resueltos: Number(t.tickets_resueltos ?? t.resueltos ?? t.count_resueltos ?? 0),
      calificacion_promedio: t.calificacion_promedio ?? t.calificacion ?? t.avg_calificacion ?? null,
    }));
  };

  const normalizeUsuariosStats = (arr) => {
    // Esperado: [{ id_usuario, nombre, tickets_creados, calificacion_promedio }, ...]
    return (arr || []).map((u) => ({
      id_usuario: u.id_usuario ?? u.id ?? null,
      nombre: u.nombre ?? u.nombre_usuario ?? "Sin nombre",
      tickets_creados: Number(u.tickets_creados ?? u.tickets ?? u.count_tickets ?? 0),
      calificacion_promedio: u.calificacion_promedio ?? u.calificacion ?? u.avg_calificacion ?? null,
    }));
  };

  if (loading) return <p className="text-center mt-4">Cargando dashboard...</p>;
  if (error) return <p className="text-danger text-center mt-4">{error}</p>;

  return (
    <div className="dashboard-container">
      <h1 className="titulo-pagina">Estadísticas Generales</h1>

      <div className="stats-grid">
        <div className="stat-card">
          <h4>Usuarios</h4>
          <p>{stats.usuarios}</p>
        </div>

        <div className="stat-card">
          <h4>Técnicos</h4>
          <p>{stats.tecnicos}</p>
        </div>

        <div className="stat-card">
          <h4>En Proceso</h4>
          <p>{stats.enProceso}</p>
        </div>

        <div className="stat-card">
          <h4>Cerrados</h4>
          <p>{stats.cerrados}</p>
        </div>

        <div className="stat-card">
          <h4>Cancelados</h4>
          <p>{stats.cancelados}</p>
        </div>

        <div className="stat-card">
          <h4>⭐ Satisfacción</h4>
          <p>{stats.promedioSatisfaccion}</p>
        </div>
      </div>

      <hr />

      <h2 className="subtitulo">Estadísticas por Técnico</h2>
      <div className="cards-container">
        {statsTecnicos.map((t) => (
          <div className="card-theme user-card" key={t.id_usuario ?? Math.random()}>
            <h5>{t.nombre}</h5>
            <p>
              Tickets resueltos: <strong>{t.tickets_resueltos}</strong>
            </p>
            <p>
              ⭐ Promedio:{" "}
              <strong>{t.calificacion_promedio ? Number(t.calificacion_promedio).toFixed(2) : "N/A"}</strong>
            </p>
          </div>
        ))}
      </div>

      <hr />

      <h2 className="subtitulo">Estadísticas por Usuario</h2>
      <div className="cards-container">
        {statsUsuarios.map((u) => (
          <div className="card-theme user-card" key={u.id_usuario ?? Math.random()}>
            <h5>{u.nombre}</h5>
            <p>
              Tickets creados: <strong>{u.tickets_creados}</strong>
            </p>
            <p>
              ⭐ Calificación recibida:{" "}
              <strong>{u.calificacion_promedio ? Number(u.calificacion_promedio).toFixed(2) : "N/A"}</strong>
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
